package p.Part2;

public class CounterEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=8;
		int b=5;
		
		System.out.println("a+b="+(a+b));
		System.out.println("a-b="+(a-b));
		System.out.println("axb="+(a*b));
		System.out.println("a/b="+(a/b));
		System.out.println("a%b="+(a%b));
	}

}
